import datetime
import json

import pytest
from fastapi.testclient import TestClient
from newsfetch_core.api_schemas import Article

import config
from core.database import Base, engine
from fastapi_main import app

client = TestClient(app)


@pytest.fixture()
def test_db():
    Base.metadata.create_all(bind=engine)
    yield
    Base.metadata.drop_all(bind=engine)


DEFAULT_LIMIT = 10
TEST_SAMPLE_SIZE = 20


class TestApi():
    def test_health_check(self):
        response = client.get("/health-check")
        assert response.status_code == 200
        assert response.json() == {"status": "OK"}

    def test_create_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.post("/article", json=article_json)
        assert response.status_code == 200
        response_article = Article(**response.json())
        assert response_article.url == article.url
        assert response_article.title == article.title
        assert response_article.authors == article.authors
        assert response_article.content == article.content

    def get_test_article(self):
        with(open("processed_warc_news_sample_article.json")) as f:
            article = Article(**json.load(f))
        return article

    def test_get_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.post("/article", json=article_json)
        assert response.status_code == 200

        response = client.get(f"/article?url={article.url}")
        assert response.status_code == 200
        response_article = Article(**response.json())
        assert response_article.url == article.url
        assert response_article.title == article.title
        assert response_article.authors == article.authors
        assert response_article.content == article.content

    def test_delete_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.post("/article", json=article_json)
        assert response.status_code == 200
        response = client.get(f"/article?url={article.url}")
        assert response.status_code == 200
        response = client.delete(f"/article?url={article.url}")
        assert response.status_code == 200
        response = client.get(f"/article?url={article.url}")
        assert response.status_code == 404

    def test_create_article_existing_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.post("/article", json=article_json)
        assert response.status_code == 200
        response = client.post("/article", json=article_json)
        assert response.status_code == 400

    def test_update_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.post("/article", json=article_json)
        assert response.status_code == 200
        article.authors = ['James Bond', "Jane Bond"]
        article_json = json.loads(article.json())
        response = client.put("/article", json=article_json)
        assert response.status_code == 200
        response_article = Article(**response.json())
        assert response_article.authors == article.authors

    def test_update_article_non_existant_article(self, test_db):
        article = self.get_test_article()
        article_json = json.loads(article.json())
        response = client.put("/article", json=article_json)
        assert response.status_code == 400

    def test_article_search(self, test_db):
        for i in range(TEST_SAMPLE_SIZE):
            article = Article(url="https://www.example.com/{}".format(i + 1),
                              title="Example Article " + str(i + 1),
                              authors=['John Doe' + str(i + 1), 'Jane Doe' + str(i + 1)],
                              content="Example Content" + str(i + 1),
                              excerpt="Example Excerpt" + str(i + 1),
                              content_length=100,
                              published_date=datetime.datetime.strptime("2020-01-" + str(i + 1 + 10), "%Y-%m-%d"),
                              language="en",
                              domain="example.com",
                              media="https://www.example.com/media/{}".format(i + 1),
                              meta="")
            article_json = json.loads(article.json())
            response = client.post("/article", json=article_json)
            assert response.status_code == 200

        response = client.get(f"/articles/search")
        assert response.status_code == 200
        assert len(response.json()) == 10

        # limit
        limit = 5
        response = client.get(f"/articles/search?limit={limit}")
        assert response.status_code == 200
        assert len(response.json()) == limit
        assert response.json()[0]["title"] == "Example Article 1"
        assert response.json()[-1]["title"] == "Example Article 5"

        # skip
        skip = 1
        response = client.get(f"/articles/search?skip={skip}")
        assert response.status_code == 200
        assert len(response.json()) == DEFAULT_LIMIT
        assert response.json()[0]["title"] == "Example Article 2"
        assert response.json()[-1]["title"] == "Example Article 11"

        # limit greater than default
        limit = DEFAULT_LIMIT + 1
        response = client.get(f"/articles/search?limit={limit}")
        assert response.status_code == 200
        assert len(response.json()) == limit
        assert response.json()[-1]["title"] == "Example Article 11"

        # limit greater than default max
        limit = config.DEFAULT_MAX_LIMIT + 1
        response = client.get(f"/articles/search?limit={limit}")
        assert response.status_code == 200
        assert len(response.json()) == TEST_SAMPLE_SIZE
        assert response.json()[-1]["title"] == "Example Article 20"

        # limit greater than size
        limit = TEST_SAMPLE_SIZE + TEST_SAMPLE_SIZE
        response = client.get(f"/articles/search?limit={limit}")
        assert response.status_code == 200
        assert len(response.json()) == TEST_SAMPLE_SIZE
        assert response.json()[-1]["title"] == "Example Article 20"

        # start date
        response = client.get(f"/articles/search?start_date=2020-01-23T00:00:00")
        assert response.status_code == 200
        assert len(response.json()) == 8
        assert response.json()[0]["title"] == "Example Article 13"
        assert response.json()[-1]["title"] == "Example Article 20"

        # end date
        response = client.get(f"/articles/search?end_date=2020-01-15T00:00:00")
        assert response.status_code == 200
        assert len(response.json()) == 5
        assert response.json()[0]["title"] == "Example Article 1"
        assert response.json()[-1]["title"] == "Example Article 5"

        # start date and end date
        response = client.get(f"/articles/search?start_date=2020-01-11T00:00:00&end_date=2020-01-13T00:00:00")
        assert response.status_code == 200
        assert len(response.json()) == 3
        assert response.json()[0]["title"] == "Example Article 1"
        assert response.json()[-1]["title"] == "Example Article 3"

        # title contains
        response = client.get(f"/articles/search?q=Article 1")
        assert response.status_code == 200
        assert len(response.json()) == 10
        assert response.json()[0]["title"] == "Example Article 1"
        assert response.json()[-1]["title"] == "Example Article 18"
        response = client.get(f"/articles/search?q=Article 2")
        assert response.status_code == 200
        assert len(response.json()) == 2
        assert response.json()[0]["title"] == "Example Article 2"
        assert response.json()[-1]["title"] == "Example Article 20"
        response = client.get(f"/articles/search?q=Article 3")
        assert response.status_code == 200
        assert len(response.json()) == 1
        assert response.json()[0]["title"] == "Example Article 3"

